import axios from 'axios'
import React, { useEffect, useState } from 'react'
import {Link} from '@reach/router'

const PetDetails = props =>{
    const [usePet, setUsePet] = useState();

    useEffect(() =>{
        axios.get('http://localhost:8000/api/pet/' + props.id)
            .then(res =>{
                setUsePet(res.data.pets)
            })
    }, [])

    const axiosDelete = (petId) =>{
        axios.delete('http://localhost:8000/api/delete/' + petId)
            .then(res =>{
                props.deleteProduct(petId)
            }) 
    }
    if(usePet){    
        return(
            <div>
                <h1>Pet Shelter</h1>
                <button onClick={(e) => {axiosDelete(usePet._id)}}>Adopt Pet</button>
                <Link to='/'>Back to Home</Link>
                <h3>Details about: {usePet.name} </h3>
                <p>Pet Type: {usePet.type}</p>
                <p>Description: {usePet.description}</p>
                <p>Skills: {usePet.skill1} {usePet.skill2} {usePet.skill3}</p>
            </div>
        )
    }
    else{
        return(
            <div>
                <h1>Loading...</h1>
            </div>
        )
    }
}
export default PetDetails
